<?php

namespace Stonkeep\Clicksign;

use Illuminate\Support\Facades\File;
use Illuminate\Support\Facades\Http;
use Illuminate\Support\Facades\Storage;

class Clicksign
{
    private $accessToken;
    private $url = '/api/v1/documents?access_token=';
    private $urlBase;
    /**
     * @var string
     */
    private $fullUrl;

    public function __construct()
    {
        try {
            $this->accessToken = env('CLICKSIGN_ACCESS_TOKEN');
            throw_if(is_null($this->accessToken), 'Não possui chave de acesso. Favor verificar');
        } catch (\Exception $e) {
            return;
        }
        $this->urlBase = env('CLICKSIGN_DEV_MODE', true) ? env('CLICKSIGN_DEV_URL', 'https://sandbox.clicksign.com') : env('CLICKSIGN_PROD_URL', 'https://app.clicksign.com');
        $this->fullUrl = $this->urlBase . $this->url . $this->accessToken;
    }

    /**
     *
     */
    public function createDocument()
    {
        dd(File::get('file.pdf'));
        $body = [
            "document" => [
                "path" => "/Contrato de Prestação de Serviços-123.pdf",
                "content_base64" => "data:application/pdf;base64,",
                "deadline_at" => "2020-11-05T14:30:59-03:00",
                "auto_close" => true,
                "locale" => "pt-BR",
                "sequence_enabled" => false
            ]
        ];
        $response = Http::post("$this->urlBase/api/v1/documents?access_token=eb570765-21cb-4f1a-a6e3-704550d8a30a", $body);
        dd($response->body());
    }
}
